		<iso:rule context="escidoc:start-date">
			<iso:assert test=". = '''' or (matches(., ''^\\d\\d\\d\\d(-\\d\\d){0,2}$'') and substring(concat(., ''-01-01''), 1, 10) castable as xs:date)">DateFormatIncorrect</iso:assert>
		</iso:rule>
		<iso:rule context="escidoc:end-date">
			<iso:assert test=". = '''' or (matches(., ''^\\d\\d\\d\\d(-\\d\\d){0,2}$'') and substring(concat(., ''-01-01''), 1, 10) castable as xs:date)">DateFormatIncorrect</iso:assert>
