		<iso:rule context="escidocMetadataProfile:publication">
			<iso:assert test="not(@type = ''article'' or @type = ''book-item'' or @type = ''conference-paper'') or (publication:source/dc:title != '''')">SourceNotProvided</iso:assert>
		</iso:rule>
