	<!-- Validation point specific -->	
	
	<!-- Title is required -->
	<iso:pattern name="title_required" id="title_required">
		<iso:rule context="escidocMetadataProfile:publication">
			<iso:assert test="dc:title != ''''">TitleNotProvided</iso:assert>
		</iso:rule>
