   </xsl:template>

   <!--PATTERN title_required-->


	<!--RULE -->
<xsl:template match="escidocMetadataProfile:publication" priority="4000" mode="M20">

		<!--ASSERT -->
<xsl:choose>
         <xsl:when test="dc:title != ''''"/>
         <xsl:otherwise>
            <failure infolevel="restrictive">
               <message>
                  <name>TitleNotProvided</name>
