DROP TABLE IF EXISTS public.user_login;
CREATE TABLE public.user_login (loginname character varying(255) NOT NULL, password character varying(255), last_password_change DATE, password_change_flag BOOLEAN);
ALTER TABLE public.user_login OWNER TO postgres;
ALTER TABLE ONLY public.user_login ADD CONSTRAINT user_login_pkey PRIMARY KEY (loginname);
ALTER TABLE ONLY public.user_login ADD CONSTRAINT user_login_loginname_fkey2 FOREIGN KEY (loginname) REFERENCES user_account(loginname) ON UPDATE CASCADE ON DELETE CASCADE;

DROP TABLE public.id_provider;
CREATE TABLE public.id_provider (type character varying(255) NOT NULL, current_id bigint);
ALTER TABLE public.id_provider OWNER TO postgres;
ALTER TABLE ONLY public.id_provider ADD CONSTRAINT id_provider_key PRIMARY KEY (type);








